INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330774, 'Acitretin', 'Trabectedin', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330775, 'Acitretin', 'Tretinoin', 'Major', 'Concomitant use of a retinoid with vitamin A or other retinoids may result in hypervitaminosis A and additive toxicities. Patients may be at increased risk for potentially serious adverse effects such as vision impairment, pseudotumor cerebri (benign intracranial hypertension), mucositis, esophagitis, hepatitis, hypertriglyceridemia, pancreatitis, ileitis, and colitis.', 'DDInter', 1767317475, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance. Patients should be made aware of vitamin A sources in vitamin supplements, fortified foods and over-the-counter medications, and be advised to contact their physician if they experience signs of hypervitaminosis A such as inflammation or bleeding of the gums or lips; dry, scaly skin; alopecia; pruritus; erythema; headache; vertigo; and nausea. Patients should discontinue the medication and seek immediate medical attention if they develop signs and symptoms of pseudotumour cerebri or hepatotoxicity.', 'Synergism', 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330776, 'Acitretin', 'Verteporfin', 'Moderate', 'Concomitant use of verteporfin with other known photosensitizing agents may increase the risk of photosensitivity reactions. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 1767317475, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of skin or eyes to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) for 5 days after administration of verteporfin.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330777, 'Acitretin', 'Vitamin A', 'Major', 'Concomitant use of a retinoid with vitamin A or other retinoids may result in hypervitaminosis A and additive toxicities. Patients may be at increased risk for potentially serious adverse effects such as vision impairment, pseudotumor cerebri (benign intracranial hypertension), mucositis, esophagitis, hepatitis, hypertriglyceridemia, pancreatitis, ileitis, and colitis.', 'DDInter', 1767317475, 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance. Patients should be made aware of vitamin A sources in vitamin supplements, fortified foods and over-the-counter medications, and be advised to contact their physician if they experience signs of hypervitaminosis A such as inflammation or bleeding of the gums or lips; dry, scaly skin; alopecia; pruritus; erythema; headache; vertigo; and nausea. Patients should discontinue the medication and seek immediate medical attention if they develop signs and symptoms of pseudotumour cerebri or hepatotoxicity.', 'Synergism', 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330778, 'Acrivastine', 'Aclidinium', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', 'DDInter', 1767317475, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, NULL, 'Moderate', 'pharmacodynamic');
