INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330823, 'Insulin human (inhalation, rapid acting)', 'Aclidinium', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', 'DDInter', 1767317475, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
