INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330854, 'Aclidinium', 'Prucalopride', 'Moderate', 'Concomitant administration of anticholinergic agents would be expected to reduce the therapeutic effects of prucalopride. Prucalopride increases bowel motility by stimulating colonic peristalsis (high-amplitude propagating contractions), an action that has been shown to be facilitated by acetylcholine release in isolated gastrointestinal tissues from various animal species.', 'DDInter', 1767317475, 'Monitoring for altered efficacy of prucalopride may be advisable during concomitant use with anticholinergic agents.', 'Absorption', 'Monitoring for altered efficacy of prucalopride may be advisable during concomitant use with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
