INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11512, 'Dezocine', 'Butalbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11516/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11513, 'Dicoumarol', 'Butalbital', 'Major', 'Barbiturates reduce the effects of oral anticoagulants by inducing their hepatic metabolism. Anticoagulant dosage requirements may be increased by 30% to 60%. After the barbiturate is discontinued, excessive anticoagulation and bleeding may occur if the anticoagulant dose is not reduced.', NULL, 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued.', NULL, 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued. Alternatively, a benzodiazepine might be used instead of a barbiturate, if appropriate. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11517/', '[1] Antlitz AM, Tolentino M, Kosai MF "Effect of butabarbital on orally administered anticoagulants." Curr Ther Res Clin Exp 10 (1968): 70-3[2] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994): 676-83[3] O''Reilly RA, Trager WF, Motley CH, Howald W "Interaction of secobarbital with warfarin pseudoracemates." Clin Pharmacol Ther 28 (1980): 187-95[4] Blouin R, Chaudhary I, Nishihara K, Cox S "The effects of liver and renal disease on stereoselective serum binding of flurbiprofen." Br J Clin Pharmacol 35 (1