INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11679, 'Voriconazole', 'Loratadine', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', NULL, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11683/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[8] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[9] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11680, 'Alemtuzumab', 'Albendazole', 'Moderate', 'The use of alemtuzumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Alemtuzumab alone may cause severe and prolonged myelosuppression, lymphopenia, and rarely, fatal bone marrow aplasia/hypoplasia, autoimmune idiopathic thrombocytopenia, and autoimmune hemolytic anemia. Serious, sometimes fatal opportunistic infections have been reported, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. The manufacturer recommends that single doses of alemtuzumab not exceed 30 mg and cumulative weekly doses not exceed 90 mg, since higher dosages are associated with an increased incidence of pancytopenia. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11684/', '[1] "Product Information. Campath (alemtuzumab)" Berlex, Richmond, CA.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767365203);
