INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11745, 'Albendazole', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11749/', '[1] "Product Information. Xofigo (radium Ra 223 dichloride)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11746, 'Albendazole', 'Ribociclib', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to ribociclib-mediated inhibition of CYP450 3A4 metabolism.', NULL, 'Caution is advised when ribociclib is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ribociclib is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ribociclib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11750/', '[1] Rawden HC, Kokwaro GO, Ward SA, Edwards G "Relative contribution of cytochromes P-450 and flavin-containing monoxygenases to the metabolism of albendazole by human liver microsomes." Br J Clin Pharmacol 49 (2000): 313-22[2] Trivier JM, Libersa C, Belloc C, Lhermitte M "Amiodarone N-deethylation in human liver microsomes: involvement of cytochrome P450 3A enzymes (first report)." Life Sci 52 (1993): pl91-6[3] Kane GC, Lipsky JJ "Drug-grapefruit juice interactions." Mayo Clin Proc 75 (2000): 933-42[4] Katoh M, Nakajima M, Yamazaki H, Yokoi T "Inhibitory effects of CYP3A4 substrates and their metabolites on P-glycoprotein-mediated transport." Eur J Pharm Sci 12 (2001): 505-13[5] DSouza DL, Levasseur LM, Nezamis J, Robbins DK, Simms L, Koch KM "Effect of alosetron on the pharmacokinetics of alprazolam." J Clin Pharmacol 41 (2001): 452-4[6] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[7] Yu DK "The contribution of P-glycoprotein to pharmacokinetic drug-drug interactions." J Clin Pharmacol 39 (1999): 1203-11[8] Zhou XJ, Zhou-Pan XR, Gauthier T, Placidi M, Maurel P, Rahmani R "Human liver microsomal cytochrome P450 3A isozymes mediated vindesine biotransformation. Metabolic drug interactions." Biochem Pharmacol 45 (1993): 853-61[9] Nagy J, Schipper HG, Koopmans RP, Butter JJ, van Boxtel CJ, Kager PA "Effect of grapefruit juice or cimetidine coadministration on albendazole bioavailability." Am J Trop Med Hyg 66 (2002): 260-3[10] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11747, 'Albendazole', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11751/', '[1] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000): 1284-90[2] "Product Information. Priftin (rifapentine)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11748, 'Albendazole', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may decrease the plasma concentrations of the benzimidazoles, albendazole and mebendazole, as well as the active metabolite of albendazole, albendazole sulfoxide. The precise mechanism has not been established, but may be related to the induction of CYP450 1A2, CYP450 2C9, and/or uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) by ritonavir following chronic administration. A pharmacokinetic study evaluating the effects of a single dose of either albendazole (400 mg) (n=8) or mebendazole (1000 mg) (n=8) administered with ritonavir (200 mg twice daily for 8 days) in healthy, nonsmoking male Caucasian subjects reported a significant reduction in the systemic exposure (AUC) and maximum plasma concentration (Cmax) of both albendazole and mebendazole. Specifically, mean AUC decreased to 27% and 43% of baseline for albendazole and mebendazole, respectively, and Cmax decreased to 26% and 41% of baseline, respectively. Albendazole sulfoxide AUC and Cmax were reduced to 41% and 52% of baseline, respectively. By contrast, pharmacokinetic parameters for albendazole, mebendazole, and albendazole sulfoxide were not significantly altered following administration of ritonavir for 2 doses only. The clinical relevance of the interaction observed following chronic ritonavir administration is unknown, since therapeutic ranges to optimize efficacy have not been established for either albendazole or mebendazole. Clinical impact is expected to be minimal in the treatment of intestinal infections, but may be increased when albendazole or mebendazole is used for systemic helminthic diseases.', NULL, 'Caution and monitoring for altered clinical efficacy are recommended if albendazole or mebendazole is used in combination with ritonavir in patients being treated for systemic helminthic infections.', NULL, 'Caution and monitoring for altered clinical efficacy are recommended if albendazole or mebendazole is used in combination with ritonavir in patients being treated for systemic helminthic infections. Dose adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11752/', '[1] Corti N, Heck A, Rentsch K, et al. "Effect of ritonavir on the pharmacokinetics of the benzimidazoles albendazole and mebendazole: an interaction study in healthy volunteers." Eur J Clin Pharmacol 65 (2009): 999-1006[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Pawluk SA, Roels CA, Wilby KJ, Ensom MHH "A review of pharmacokinetic drugdrug interactions with the anthelmintic medications albendazole and mebendazole." Clin Pharmacokinet 54 (2015): 371-83[4] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11749, 'Albendazole', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11753/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767365203);
