INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12414, 'Ethanol', 'Alfentanil', 'Moderate', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics. Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills. In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', NULL, 'Concomitant use of opioid analgesics with ethanol should be avoided.', NULL, 'Concomitant use of opioid analgesics with ethanol should be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12418/', '[1] Sturner WQ, Garriott JC "Deaths involving propoxyphene: a study of 41 cases over a two-year period." JAMA 223 (1973): 1125-30[2] Edwards C, Gard PR, Handley SL, Hunter M, Whittington RM "Distalgesic and ethanol-impaired function." Lancet 2 (1982): 384[3] Rosser WW "The interaction of propoxyphene with other drugs." Can Med Assoc J 122 (1980): 149-50[4] Levine B, Saady J, Fierro M, Valentour J "A hydromorphone and ethanol fatality." J Forensic Sci 29 (1984): 655-9[5] Sellers EM, Hamilton CA, Kaplan HL, Degani NC, Foltz RL "Pharmacokinetic interaction of propoxyphene with ethanol." Br J Clin Pharmacol 19 (1985): 398-401[6] Linnoila M, Hakkinen S "Effects of diazepam and codeine, alone and in combination with alcohol, on simulated driving." Clin Pharmacol Ther 15 (1974): 368-73[7] Girre C, Hirschhorn M, Bertaux L, et al "Enhancement of propoxyphene bioavailability by ethanol: relation to psychomotor and cognitive function in healthy volunteers." Eur J Clin Pharmacol 41 (1991): 147-52[8] Kiplinger GF, Sokol G, Rodda