INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330945, 'Formoterol', 'Dexamethasone', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330946, 'Formoterol', 'Dexmethylphenidate', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317475, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
