INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1331125, 'Formoterol', 'Phendimetrazine', 'Moderate', 'Central nervous system (CNS) stimulants, particularly the amphetamines, can potentiate the adrenergic response to vasopressors and other sympathomimetic agents. Additive increases in blood pressure and heart rate may occur due to enhanced peripheral sympathetic activity.', 'DDInter', 1767317475, 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents. Pulse and blood pressure should be closely monitored.', 'Synergism', 'Caution is advised if CNS stimulants are used with vasopressors or other sympathomimetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1331126, 'Formoterol', 'Phenelzine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) can potentiate the cardiovascular adverse effects of beta-2 adrenergic agonists such as hypertension, palpitation, tachycardia, and chest pain.', 'DDInter', 1767317475, 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). Preferably, at least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with beta-2 agonists.', 'Synergism', 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
