INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (25381, 'Hyoscyamine', 'Phenylephrine (ophthalmic)', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', NULL, 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', NULL, 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/25400/', '[1] Lai YK "Adverse effect of intraoperative phenylephrine 10%: case report." Br J Ophthalmol 73 (1989): 468-9[2] Daelman F, Andrejak M, Rajaonarivony D, Bryselbout E, Jezraoui P, Ossart M "Phenylephrine eyedrops, systemic atropine and cardiovascular adverse events." Therapie 49 (1994): 467[3] Levine MA, Leenen FH "Role of vagal activity in the cardiovascular responses to phenylephrine in man." Br J Clin Pharmacol 33 (1992): 333-6[4] Van Der Spek AF, Hantler CB "Phenylephrine eyedrops and anesthesia." Anesthesiology 64