INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12534, 'Ethanol', 'Doxazosin', 'Moderate', 'The concurrent use of ethanol and alpha-1 adrenergic blockers may cause increased hypotensive effects. Patients with aldehyde dehydrogenase deficiencies (primarily Asians) may be at a higher risk of this interaction. The mechanism has not been determined. Data exist for prazosin and other alpha adrenergic blockers are expected to interact also. In addition, any patients taking alpha adrenergic blockers may experience excessive orthostatic hypotension with ethanol ingestion, due to ethanol''s unopposed vasodilatory effects in the presence of alpha adrenergic blockade.', NULL, 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake.', NULL, 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake. All patients should be warned about the possibility of orthostatic hypotension with concurrent ethanol use.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12538/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] Kawano Y, Abe H, Kojima S, Takishita S, Omae T "Interaction of alcohol and an a1-blocker on ambulatory blood pressure in patients with essential hypertension." Am J Hypertens 13 (2000): 307-12', 1767363020);
