INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12578, 'Fomepizole', 'Ethanol', 'Major', 'The manufacturer reports that oral doses of fomepizole (10 to 20 mg/kg) will significantly reduce the rate of elimination of moderate doses of ethanol by approximately 40% when given to healthy volunteers. The mechanism is via alcohol dehydrogenase inhibition. By a similar mechanism, ethanol will decrease the rate of elimination of fomepizole.', NULL, 'In general, ethanol and fomepizole should not be coadministered in the treatment of ethylene glycol poisoning.', NULL, 'In general, ethanol and fomepizole should not be coadministered in the treatment of ethylene glycol poisoning.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12582/', '[1] "Product Information. Antizol (fomepizole)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767363020);
