INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12595, 'Hydromorphone', 'Ethanol', 'Major', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including hydromorphone. Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills. In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur. Consumption of alcohol while taking sustained-release formulations of hydromorphone may cause rapid release of the drug, resulting in high systemic levels of hydromorphone that may be potentially lethal even in opioid-tolerant patients. Alcohol appears to disrupt the extended release mechanism, causing ''dose-dumping'' into the bloodstream.', NULL, 'Patients taking sustained-release formulations of hydromorphone should not consume alcohol or use medications that contain alcohol on days of hydromorphone dosing.', NULL, 'Patients taking sustained-release formulations of hydromorphone should not consume alcohol or use medications that contain alcohol on days of hydromorphone dosing. In general, potent narcotics such as hydromorphone should not be combined with alcohol.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12599/', '[1] FDA. U.S. Food and Drug Administration "Healthcare Professional Sheet. FDA Alert [07/2005]: alcohol-palladone interaction. Available from: URL: http://www.fda.gov/medwatch/SAFETY/2005/safety05.htm#Palladone." ([2005 July 13]):[2] "Product Information. Dilaudid (hydromorphone)." Knoll Pharmaceutical Company, Whippany, NJ.[3] Levine B, Saady J, Fierro M, Valentour J "A hydromorphone and ethanol fatality." J Forensic Sci 29 (1984): 655-9[4] FDA. U.S. Food and Drug Administration "Healthcare Professional Sheet. FDA Alert [07/2005]: alcohol-palladone interaction. Available from: URL: http://www.fda.gov/medwatch/SAFETY/2005/safety05.htm#Palladone." ([2005 July 13]):[5] Levine B, Saady J, Fierro M, Valentour J "A hydromorphone and ethanol fatality." J Forensic Sci 29 (1984): 655-9[6] "Product Information. Dilaudid (hydromorphone)." Knoll Pharmaceutical Company, Whippany, NJ.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12596, 'Hydroxyzine', 'Ethanol', 'Moderate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents. Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', NULL, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', NULL, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12600/', '[1] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Linnoila M "Drug effects on psychomotor skills related to driving: interaction of atropine, glycopyrrhonium and alcohol." Eur J Clin Pharmacol 6 (1973): 107-12[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12597, 'Hyoscyamine', 'Ethanol', 'Moderate', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous. In addition, the potential for abuse may be increased with the combination. The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', NULL, 'Alcohol should generally be avoided during therapy with anticholinergic agents.', NULL, 'Alcohol should generally be avoided during therapy with anticholinergic agents. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12601/', '[1] Linnoila M "Drug effects on psychomotor skills related to driving: interaction of atropine, glycopyrrhonium and alcohol." Eur J Clin Pharmacol 6 (1973): 107-12[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12598, 'Idelalisib', 'Ethanol', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12602/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12599, 'Ifosfamide', 'Ethanol', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12603/', '[1] Simonian NA, Gilliam FG, Chiappa KH "Ifosfamide causes a diazepam-sensitive encephalopathy." Neurology 43 (1993): 2700-2[2] "Product Information. Ifex (ifosfamide)." Bristol-Myers Squibb, Princeton, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology