INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (12649, 'Ethanol', 'Lovastatin', 'Moderate', 'Concomitant use of statin medication with substantial quantities of alcohol may increase the risk of hepatic injury. Transient increases in serum transaminases have been reported with statin use and while these increases generally resolve or improve with continued therapy or a brief interruption in therapy, there have been rare postmarketing reports of fatal and non-fatal hepatic failure in patients taking statins. Patients who consume substantial quantities of alcohol and/or have a history of liver disease may be at increased risk for hepatic injury. Active liver disease or unexplained transaminase elevations are contraindications to statin use.', NULL, 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', NULL, 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12653/', '[1] "Product Information. Crestor (rosuvastatin)." AstraZeneca Pharma Inc, Mississauga, ON.[2] "Product Information. Pravachol (pravastatin)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[4] "Product Information. Lescol (fluvastatin)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Altocor (lovastatin)." Andrx Pharmaceuticals, Fort La