        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1331355, 'Pseudoephedrine', 'Levodopa', 'Moderate', 'Coadministration with levodopa may potentiate the cardiovascular effects of sympathomimetic agents (e.g., epinephrine, norepinephrine, isoproterenol, amphetamine). Additive increases in blood pressure and heart rate may occur due to enhanced sympathetic activity. However, data evaluating the interaction are not available.', NULL, 'The use of levodopa with sympathomimetic agents should generally be avoided.', NULL, 'The use of levodopa with sympathomimetic agents should generally be avoided. If concomitant use is required, close monitoring of the cardiovascular system is recommended and the dose of the sympathomimetic agent may need to be reduced.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
