        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1331584, 'Iron protein succinylate', 'Doxycycline', 'Moderate', 'The bioavailability of oral tetracyclines and iron salts may be significantly decreased during concurrent administration. Therapeutic failure may result. The proposed mechanism is chelation of tetracyclines by the iron cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Coadministration of a tetracycline with any iron-containing product should be avoided if possible.', NULL, 'Coadministration of a tetracycline with any iron-containing product should be avoided if possible. Otherwise, patients should be advised to stagger the times of administration by at least three to four hours, although separating the doses may not prevent the interaction with doxycycline.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
