    ) VALUES (1331745, 'Diclofenac', 'Ketorolac', 'Major', 'The use of ketorolac in combination with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of serious adverse effects such as renal failure and gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines.', NULL, 'Concurrent use of ketorolac with other NSAIDs or aspirin is considered contraindicated.', NULL, 'Concurrent use of ketorolac with other NSAIDs or aspirin is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
