INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27146, 'Homatropine', 'Guanidine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27178/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc, Cost Mesa, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27147, 'Hydrocodone', 'Halazepam', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Benzonatate', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27179/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
