INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27232, 'Homatropine', 'Neostigmine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Latanoprost, Apraclonidine, Methazolamide, Clonidine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, Epinephrine, Brimonidine, More', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27264/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc, Cost Mesa, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27233, 'Hydrocodone', 'Nevirapine', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Benzonatate, Clofedanol, Dextromethorphan, Pentoxyverine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27265/', '[1] "Product Information. Mycobutin (rifabutin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] "Product Information. Ionsys (fentanyl)." Ortho McNeil Pharmaceutical, Raritan, NJ.[5] "Product Information. Butorphanol Tartrate (butorpha