INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27314, 'Hydrocodone', 'Scopolamine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Nitrazepam, Zopiclone, Tropicamide, Ephedrine, Ketorolac, Phenylephrine, Rolapitant', 'Benzonatate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27346/', '[1] "Product Information. Apadaz (acetaminophen-benzhydrocodone)." KemPharm, Inc, Coralville, IA.[2] "Product Information. Lortab (acetaminophen-hydrocodone)." Akorn Inc, Buffalo Grove, IL.[3] "Product Information. Exalgo (hydromorphone)." Covidien, Mansfield, MA.[4] "Product Information. Naloxone HCl-Pentazocine HCl (naloxone-pentazocine)." Actavis U.S. (Amide Pharmaceutical Inc), Little Falls, NJ.[5] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[6] "Product Information. DepoDur (m