INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27401, 'Butorphanol', 'Apalutamide', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Naltrexone, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, Dezocine, Opium, Pentazocine, Acetylsalicylic acid, Naloxone, Nalbuphine', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27454/', '[1] "Product Information. Mycobutin (rifabutin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] "Product Information. Ionsys (fentanyl)." Ortho McNeil Pharmaceutical, Rarita