INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27406, 'Capromab pendetide', 'Apalutamide', 'Moderate', 'The effect of surgical and/or medical androgen ablation on the imaging performance of Indium In 111 capromab pendetide has not been evaluated. Preliminary data suggest that hormone ablation may increase expression of Prostate Specific Membrane Antigen (PSMA)--the target of capromab pendetide--and decrease tumor expression of prostate specific antigen (PSA).', NULL, 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', NULL, 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', 'Others', 'Gallium Ga-68 gozetotide, Fludeoxyglucose (18F), Indium In-111 pentetreotide', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Relugolix, Aminoglutethimide, Exemestane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27459/', '[1] "Product Information. ProstaScint (capromab pendetide)." Aytu BioScience, Inc, Englewood, CO.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27407, 'Cariprazine', 'Apalutamide', 'Moderate', 'Coadministration with potent or moderate inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', NULL, 'Concomitant use of cariprazine with potent or moderate CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, 'Concomitant use of cariprazine with potent or moderate CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', 'Loxapine, Molindone, Thiothixene', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Exemestane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27460/', '[1] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc., Parsippany, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27408, 'Carisoprodol', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Orphenadrine, Chlorzoxazone, Baclofen, Cyclobenzaprine, Methocarbamol, Chlormezanone', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27461/', '[1] "Product Information. Erleada (apalutamide)." Janssen Biotech, Inc., Horsham, PA, PA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yoovathaworn KC, Sriwatanakul K, Thithapandha A "Influence of caffeine on aspirin pharmacokinetics." Eur J Drug Metab Pharmacokinet 11 (1986): 71-6[4] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[5] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767369485);
