INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27461, 'Docetaxel', 'Apalutamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of docetaxel, which is a substrate of the isoenzyme. Rifampin and hyperforin, a component of St. John''s wort, have been shown to significantly induce the in vitro metabolism of docetaxel in human hepatocyte cultures.', NULL, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of docetaxel underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with docetaxel is unknown.', 'Metabolism', 'Darolutamide, Toremifene, Abarelix, Relugolix, Degarelix, Nilutamide, Abiraterone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27514/', '[1] Aronson JK, Grahame-Smith DG "Clinical pharmacology: adverse drug interactions." Br Med J 282 (1981): 288-91[2] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegevil