INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27608, 'Maraviroc', 'Apalutamide', 'Major', 'Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc may have an increased risk of postural hypotension due to increased maraviroc exposure. Moreover, these patients often have cardiovascular comorbidities that could predispose them to adverse cardiovascular events triggered by postural hypotension. Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', NULL, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', NULL, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers. However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily. Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Abiraterone, Exemestane', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27661/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27609, 'Medroxyprogesterone acetate', 'Apalutamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Estetrol, Diethylstilbestrol, Histrelin, Leuprolide, Bicalutamide, Triptorelin, Goserelin', 'Darolutamide, Fulvestrant, Toremifene, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, Abiraterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27662/', '[1] Kobayashi K, Mimura N, Fujii H, et al "Role of human cytochrome P450 3A4 in metabolism of medroxyprogesterone acetate." Clin Cancer Res 6 (2000): 3297-303[2] Lundgren S, Lonning PE, Aakvaag A, Kvinnsland S, Lnning PE "Influence of aminoglutethimide on the metabolism of medroxyprogesterone acetate and megestrol acetate in postmenopausal patients with advanced breast cancer." Cancer Chemother Pharmacol 27 (1990): 101-5[3] "Product Information. Provera (medroxyprogesterone)." Pharmacia and Upjohn, Kalamazoo, MI.[4] "FFPRHC Guidance (April 2005). Drug interactions with hormonal contraception." J Fam Plann Reprod Health Care 31 (2005): 139-51[5] O''Brien MD, Guillebaud J "Contraception for women with epilepsy." Epilepsia 47 (2006): 1419-22[6] Halpenny O, Bye A, Cranny A, Feely J, Daly PA "Influence of aminoglutethimide on plasma levels of medroxyprogesterone acetate." Med Oncol Tumor Pharmacother 7 (1990): 241-7[7] "Product Information. Depo-Provera (medroxyprogesterone)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27610, 'Mefenamic acid', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Fenoprofen, Meclofenamic acid, Ketorolac, Phenylbutazone, Misoprostol, Glucosamine, Oxaprozin, Chondroitin sulfate, Nabumetone, Flurbiprofen, Rofecoxib, More', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27663/', '[1] "Product Information. Erleada (apalutamide)." Janss