INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27611, 'Mefloquine', 'Apalutamide', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of mefloquine, which is primarily metabolized by the isoenzyme.', NULL, 'The possibility of diminished therapeutic effects should be considered when mefloquine is used in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The possibility of diminished therapeutic effects should be considered when mefloquine is used in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inducer is added to or withdrawn from mefloquine therapy, and the dosage adjusted if necessary. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with mefloquine is unknown.', 'Metabolism', 'Pyrimethamine, Tafenoquine, Sulfadoxine, Atovaquone', 'Anastrozole, Fulvestrant, Exemestane, Letrozole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27664/', '[1] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[3] Schmidt LE, Dalhoff K "Food-drug interactions." Drugs 62 (2002): 1481-502[4] "Product Information. Diabinese (chlorpropamide)." Pfizer