INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27807, 'Ubrogepant', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. When ubrogepant was administered with the potent CYP450 3A4 inducer rifampin during in vivo studies, ubrogepant systemic exposure (AUC) decreased by 80%. Reduced efficacy of ubrogepant may occur.', NULL, 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', NULL, 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', 'Metabolism', 'Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Exemestane', 'Frovatriptan, Eptinezumab, Lasmiditan, Eletriptan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Clonidine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27860/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 1767369485);
