INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (27918, 'Elagolix', 'Apixaban', 'Moderate', 'Coadministration with elagolix may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by elagolix.', NULL, 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elagolix is added to or withdrawn from therapy.', 'Others', 'Clopidogrel, Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, More', 'Norethisterone, Nafarelin, Octreotide, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Relugolix, Lanreotide, Gonadorelin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27971/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.', 1767369485);
