INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1331836, 'Macimorelin', 'Ketorolac', 'Moderate', 'Drugs that directly affect the pituitary secretion of growth hormone (e.g., somatostatin, insulin, glucocorticoids, cyclooxygenase inhibitors such as aspirin and nonsteroidal anti-inflammatory drugs) and drugs that may transiently elevate growth hormone levels (e.g., clonidine, levodopa, insulin) may impact the accuracy of the macimorelin diagnostic test.', 'DDInter', 1767317475, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration.', 'Others', 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
