INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1331905, 'Probenecid', 'Ketorolac', 'Major', 'The concomitant administration of probenecid and ketorolac may cause ketorolac toxicity. Probenecid decreases the plasma clearance of ketorolac, resulting in a three-fold increase in ketorolac AUC and a two-fold increase in its half-life. Renal failure, GI ulcerations or perforation, and hemorrhage may occur.', 'DDInter', 1767317475, 'The manufacturer considers the concurrent use of probenecid and ketorolac contraindicated.', 'Others', 'The manufacturer considers the concurrent use of probenecid and ketorolac contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
