INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332066, 'Acyclovir', 'Lithium carbonate', 'Moderate', 'Acyclovir may significantly increase the serum levels of lithium. Acyclovir may competitively inhibit the glomerular filtration of lithium, and this interaction may be dependent on the dose of acyclovir.', 'DDInter', 1767317475, 'Frequent monitoring of lithium levels and close observation for signs of lithium toxicity are recommended during acyclovir therapy. Patients should be advised to notify their physician if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, tremor, or increased urination.', 'Excretion', 'Frequent monitoring of lithium levels and close observation for signs of lithium toxicity are recommended during acyclovir therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332067, 'Acyclovir', 'Memantine', 'Minor', 'Theoretically, the concomitant administration of memantine and other drugs that undergo active renal tubular secretion may result in altered plasma levels of both drugs. The clinical significance is unknown. When hydrochlorothiazide/triamterene and memantine were coadministered in a clinical study, the bioavailability of hydrochlorothiazide decreased by 20% and there was no change in memantine or triamterene bioavailability.', 'DDInter', 1767317475, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332068, 'Acyclovir', 'Meperidine', 'Moderate', 'Acyclovir may increase the plasma concentrations of meperidine and normeperidine, the metabolite that has been associated with adverse effects such as lethargy, agitation, and seizures. The mechanism of interaction is unknown. Increased pharmacologic and adverse effects of meperidine may occur.', 'DDInter', 1767317475, 'Caution is recommended if meperidine is used with acyclovir or its prodrug, valacyclovir. Patients should be monitored for tremor, seizures, CNS and respiratory depression, and cardiac arrhythmias.', 'Others', 'Caution is recommended if meperidine is used with acyclovir or its prodrug, valacyclovir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332069, 'Acyclovir', 'Mephenytoin', 'Moderate', 'A case report suggests that plasma phenytoin concentrations may be decreased in the presence of acyclovir. Seizure activity may increase. The mechanism is unknown. A similar interaction may theoretically occur with other hydantoins.', 'DDInter', 1767317475, 'The clinician should consider closer clinical and laboratory monitoring if acyclovir therapy is started or stopped in the presence of phenytoin. The dose of phenytoin should be adjusted as clinically indicated. Patients should be advised to report loss of seizure control.', 'Others', 'The clinician should consider closer clinical and laboratory monitoring if acyclovir therapy is started or stopped in the presence of phenytoin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332070, 'Acyclovir', 'Mesalazine', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', 'DDInter', 1767317475, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332071, 'Acyclovir', 'Mycophenolate mofetil', 'Minor', 'Coadministration with mycophenolic acid may increase the plasma concentrations of acyclovir. The mechanism of interaction has not been described. However, because both MPAG and acyclovir plasma concentrations are increased in the presence of renal impairment, the potential exists for the two drugs to compete for active tubular secretion, further increasing the concentrations of both drugs. Caution may be appropriate in patients with decreased renal function.', 'DDInter', 1767317475, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332072, 'Acyclovir', 'Mycophenolic acid', 'Minor', 'Coadministration with mycophenolic acid may increase the plasma concentrations of acyclovir. The mechanism of interaction has not been described. However, because both MPAG and acyclovir plasma concentrations are increased in the presence of renal impairment, the potential exists for the two drugs to compete for active tubular secretion, further increasing the concentrations of both drugs. Caution may be appropriate in patients with decreased renal function.', 'DDInter', 1767317475, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
