INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332084, 'Acyclovir', 'Talimogene laherparepvec', 'Moderate', 'Antiherpetic antiviral agents may interfere with the therapeutic effects of talimogene laherparepvec, which is a live, attenuated herpes simplex virus.', 'DDInter', 1767317475, 'Because talimogene laherparepvec is sensitive to antiviral agents such as acyclovir, the risks and benefits of talimogene laherparepvec should be considered before administering antiviral agents to manage herpetic infection.', 'Antagonism', 'Because talimogene laherparepvec is sensitive to antiviral agents such as acyclovir, the risks and benefits of talimogene laherparepvec should be considered before administering antiviral agents to manage herpetic infection.', NULL, NULL, 'Moderate', 'pharmacodynamic');
