INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332087, 'Acyclovir', 'Tizanidine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may significantly increase the plasma concentrations and pharmacologic effects of tizanidine, which is a sensitive substrate of the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided. Otherwise, caution is advised if coadministration is required. Dosage adjustments may be necessary in patients who experience excessive adverse effects of tizanidine such as drowsiness, dizziness, lightheadedness, hypotension, and bradycardia.', 'Metabolism', 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332088, 'Acyclovir', 'Trospium', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', 'DDInter', 1767317475, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332089, 'Acyclovir', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'Concomitant use of live, attenuated varicella virus vaccine or zoster vaccine with acyclovir, famciclovir, or valacyclovir may result in diminished immunologic response to the vaccine. Although clinical data are lacking, theoretical concern exists that antiviral activity against the vaccine strain may prevent a sufficient degree of replication to occur in order to induce a protective immune response. Acyclovir, specifically, has demonstrated activity against the Oka strain of live, attenuated varicella-zoster virus used in the vaccines.', 'DDInter', 1767317475, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine. In addition, these antiviral agents should generally be avoided for 14 days after vaccination.', 'Antagonism', 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine.', NULL, NULL, 'Major', 'pharmacodynamic');
