INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14085, 'Amiodarone', 'Alfentanil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of alfentanil, which is primarily metabolized by the isoenzyme. Alfentanil clearance has been reported to reduce significantly in the presence of moderate or potent inhibitors of CYP450 3A4 such as cimetidine, diltiazem, fluconazole, and troleandomycin. Isolated reports of prolonged sedation and respiratory depression have also been associated with concomitant use of inhibitors such as erythromycin.', NULL, 'Patients receiving alfentanil in combination with CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', NULL, 'Patients receiving alfentanil in combination with CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary. Recovery time from alfentanil anesthesia may be prolonged in some cases.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14099/', '[1] Kharasch ED, Thummel KE "Human alfentanil metabolism by cytochrome P450 3A3/4. An explanation for the interindividual variability in alfentanil clearance?" Anesth Analg 76 (1993): 1033-9[2] Bartkowski RR, McDonnell TE "Prolonged alfentanil effect following erythromycin administration." Anesthesiology 73 (1990): 566-8[3] Bartkowski RR, Goldberg ME, Larijani GE, Boerner T "Inhibition of alfentanil metabolism by erythromycin." Clin Pharmacol Ther 46 (1989): 99-102[4] EMEA. Euro