INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14132, 'Alfentanil', 'Bupivacaine', 'Minor', 'Concomitant use of lidocaine theoretically may decrease the clearance and increase the elimination half-life of alfentanil. The mechanism is thought to be inhibition of the cytochrome P-450 isoenzyme 3A3/4. If lidocaine is used in a patient who has been receiving alfentanil for an extended period of time, the patient should be monitored more closely for excessive alfentanil effects (i.e., central nervous system depression, respiratory depression). Other amide-type local anesthetics may interact with alfentanil in a similar manner.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14146/', '[1] Yun CH, Wood M, Wood AJ, Guengerich FP "Identification of the pharmacogenetic determinants of alfentanil metabolism: cytochrome P-450 3A4: an explanation of the variable elimination clearance." Anesthesiology 77 (1992): 467-74[2] Kharasch ED, Thummel KE "Human alfentanil metabolism by cytochrome P450 3A3/4. An explanation for the interindividual variability in alfentanil clearance?" Anesth Analg 76 (1993): 1033-9[3] Koehntop DE, Noormohamed SE, Fletcher CV "Effects of long-term drugs on alfentanil clearance in patients undergoing renal transplantation." Pharmacotherapy 14 (1994): 592-9', 1767363020);
