INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14346, 'Alfentanil', 'Methylphenobarbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14360/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14347, 'Alfentanil', 'Mepivacaine', 'Minor', 'Concomitant use of lidocaine theoretically may decrease the clearance and increase the elimination half-life of alfentanil. The mechanism is thought to be inhibition of the cytochrome P-450 isoenzyme 3A3/4. If lidocaine is used in a patient who has been receiving alfentanil for an extended period of time, the patient should be monitored more closely for excessive alfentanil effects (i.e., central nervous system depression, respiratory depression). Other amide-type local anesthetics may interact with alfentanil in a similar manner.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14361/', '[1] Yun CH, Wood M, Wood AJ, Guengerich FP "Identification of the pharmacogenetic determinants of alfentanil metabolism: cytochrome P-450 3A4: an explanation of the variable elimination clearance." Anesthesiology 77 (1992): 467-74[2] Kharasch ED, Thummel KE "Human alfentanil metabolism by cytochrome P450 3A3/4. An explanation for the interindividual variability in alfentanil clearance?" Anesth Analg 76 (1993): 1033-9[3] Koehntop DE, Noormohamed SE, Fletcher CV "Effects of long-term drugs on alfentanil clearance in patients undergoing renal transplantation." Pharmacotherapy 14 (1994): 592-9', 1767363020);
