INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332688, 'Saxagliptin', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may alter the plasma concentrations of saxagliptin and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to saxagliptin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The physician should be notified if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to saxagliptin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332689, 'Secobarbital', 'Nifedipine', 'Moderate', 'Barbiturates may increase the hepatic metabolism of some dihydropyridine calcium channel blockers by inducing CYP450 3A4 hepatic metabolism. The serum concentrations and effects of these calcium channel blockers may be significantly decreased.', 'DDInter', 1767317475, 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted. An increased dosage of the calcium channel blocker may be necessary.', 'Metabolism', 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted.', NULL, NULL, 'Moderate', 'pharmacodynamic');
