INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332692, 'Selexipag', 'Nifedipine', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317475, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1332693, 'Selpercatinib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis), and the dosing of selpercatinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
