INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14587, 'Anagrelide', 'Fluvoxamine', 'Moderate', 'Theoretically, coadministration with drugs that are inhibitors of CYP450 1A2 may increase the plasma concentrations of anagrelide, which is a substrate of the isoenzyme.', NULL, 'Clinicians should recognize the potential for interaction with drugs that inhibit CYP450 1A2 and monitor for evidence of anagrelide toxicities during coadministration.', NULL, 'Clinicians should recognize the potential for interaction with drugs that inhibit CYP450 1A2 and monitor for evidence of anagrelide toxicities during coadministration. Since platelet reduction from anagrelide therapy is dose-related, thrombocytopenia may occur due to reduced clearance of anagrelide and may be a sign of overdosage. Patients should also be monitored for cardiovascular adverse effects such as tachycardia, palpitations, hypotension, edema, and congestive heart failure.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14601/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 1767365203);
