INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14640, 'Fluvoxamine', 'Cilostazol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes. The possibility of prolonged and/or increased pharmacologic effects of cilostazol should be considered.', NULL, 'A 50% dosage reduction of cilostazol (i.', NULL, 'A 50% dosage reduction of cilostazol (i.e., 50 mg twice a day) should be considered when used with potent or moderate CYP450 3A4 and/or 2C19 inhibitors. Close clinical and laboratory monitoring is advised whenever the inhibitor is added to or withdrawn from therapy, and the cilostazol dosage adjusted as necessary. Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14654/', '[1] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 53-9[2] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999): 61-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.[5] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[6] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[7] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[10] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14641, 'Fluvoxamine', 'Cinacalcet', 'Moderate', 'Coadministration with cinacalcet may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by cinacalcet, which is expected to occur in patients who are CYP450 2D6 extensive metabolizers (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent).', NULL, 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.', NULL, 'Caution is advised if cinacalcet must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range (e.g., class IC antiarrhythmic agents, phenothiazines, certain beta blockers, and most tricyclic antidepressants). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cinacalcet is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14655/', '[1] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sensipar (cinacalcet)." Amgen USA, Thousand Oaks, CA.[4] "Product Information. Sensipar (cinacalcet)." Amgen USA, Thousand Oaks, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14642, 'Fluvoxamine', 'Citalopram', 'Major', 'Coadministration with CYP450 2C19 inhibitors may increase the plasma concentrations of citalopram, which is partially metabolized by the isoenzyme. Cases of QT interval prolongation and torsade de pointes have been reported during postmarketing use. In general, the risk of ventricular arrhythmia in association with QT prolongation is largely unpredictable, but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia).', NULL, 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole.', NULL, 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole. Alternatives should be considered when possible, and hypokalemia or hypomagnesemia should be corrected prior to initiation of citalopram treatment and periodically monitored. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter'