INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14666, 'Fluvoxamine', 'Delavirdine', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14680/', '[1] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc, Marietta, GA.[2] Damkier P, Hansen LL, Brosen K "Effect of fluvoxamine on the pharmacokinetics of quinidine." Eur J Clin Pharmacol 55 (1999): 451-6[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14667, 'Fluvoxamine', 'Desipramine', 'Major', 'Coadministration with fluvoxamine may significantly increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is fluvoxamine inhibition of multiple hepatic microsomal enzymes involved in the metabolic clearance of TCAs. Pharmacodynamically, the combination of fluvoxamine (or any other selective serotonin reuptake inhibitor) and a TCA may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', NULL, 'In general, the use of fluvoxamine (or other SSRIs) with TCAs should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the use of fluvoxamine (or other SSRIs) with TCAs should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Pharmacologic response and plasma TCA levels should be monitored more closely whenever fluvoxamine is added to or withdrawn from therapy in patients stabilized on their existing antidepressant regimen, and the TCA dosage adjusted as necessary. Patients should be monitored closely for signs and symptoms of TCA toxicity (e.g., sedation, dry mouth, blurred vision, constipation, urinary retention) and/or excessive serotonergic activity (e.g., CNS irritability, altered consciousness, confusion, myoclonus, ataxia, abdominal cramping, hyperpyrexia, shivering, pupillary dilation, diaphoresis, hypertension, and tachycardia).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 