INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14733, 'Fondaparinux', 'Deferasirox', 'Major', 'Coadministration with drugs that have known ulcerogenic or hemorrhagic potential may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14747/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14734, 'Fondaparinux', 'Defibrotide', 'Major', 'Coadministration of defibrotide with anticoagulants may increase the risk of bleeding. In vitro, defibrotide increased the activity of fibrinolytic enzymes; therefore, it may enhance the pharmacologic effects of antithrombotic agents.', NULL, 'Concomitant use of defibrotide with systemic anticoagulants (except for routine maintenance or reopening of central venous lines) is considered contraindicated.', NULL, 'Concomitant use of defibrotide with systemic anticoagulants (except for routine maintenance or reopening of central venous lines) is considered contraindicated. Anticoagulants should be discontinued prior to initiation of defibrotide. Preferably, defibrotide administration should be delayed until the effects of anticoagulant therapy have subsided. Some authorities recommend avoiding use of anticoagulants within 24 hours of defibrotide administration, or within 12 hours in the case of unfractionated heparin. Patients should be monitored for signs of bleeding during defibrotide therapy. If bleeding develops, defibrotide should be discontinued and treatment/supportive care provided until the bleeding stops.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14748/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Defitelio (defibrotide)." Jazz Pharmaceuticals, Palo Alto, CA.[3] Bodiford AB, Kessler FO, Fermo JD, Ragucci KR "Elevated international normalized ratio with the consumption of grapefruit and use of warfarin." SAGE Open Med Case Rep 0 (2013): 1-3[4] Guo LQ, Yamazoe Y "Inhibition of cyto