INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14759, 'Fosamprenavir', 'Atazanavir', 'Moderate', 'Coadministration with fosamprenavir may decrease the plasma concentrations of atazanavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The mechanism has not been described. Atazanavir reportedly does not significantly affect the pharmacokinetics of amprenavir from fosamprenavir.', NULL, 'Appropriate dosages of atazanavir, fosamprenavir, and ritonavir with respect to safety and efficacy have not been established when used in combination.', NULL, 'Appropriate dosages of atazanavir, fosamprenavir, and ritonavir with respect to safety and efficacy have not been established when used in combination. Limited data suggest that an atazanavir dosage of 150 or 200 mg twice daily may be adequate in combination with fosamprenavir 700 mg and ritonavir 100 mg twice daily. Therapeutic drug monitoring is recommended if this regimen is used, and antiretroviral response should be monitored closely.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14773/', '[1] Ray JE, Marriott D, Bloch MT, McLachlan AJ "Therapeutic drug monitoring of atazanavir: surveillance of pharmacotherapy in the clinic." Br J Clin Pharmacol 60 (2005): 291-9[2] Khanlou H, Bhatti L, Farthing C "Interaction between atazanavir and fosamprenavir in the treatment of HIV-infected patients." J Acquir Immune Defic Syndr 41 (2006): 124-5[3] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365203);
