INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14767, 'Fosamprenavir', 'Bortezomib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bortezomib, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, 'Pharmacologic response to bortezomib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be monitored for the development of potential bortezomib toxicities such as peripheral neuropathy, orthostatic hypotension, thrombocytopenia, and neutropenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14781/', '[1] Pekol T, Daniels JS, Labutti J, et al "Human metabolism of the proteasome inhibitor bortezomib: identification of circulating metabolites." Drug Metab Dispos 33 (2005): 771-7[2] "Product Information. Velcade (bortezomib)." Millennium Pharmaceuticals Inc, Cambridge, MA.[3] Uttamsingh V, Lu C, Miwa GT, Gan LS "Relative contributions of the five major human cytochromes P450, 1A2, 2C9, 2C19, 2D6, and 3A4 to the hepatic metabolism of teh protosome inhibitor bortezomib." Drug Metab Dispos 33 (2005): 1723-8', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14768, 'Fosamprenavir', 'Bosentan', 'Moderate', 'Coadministration of a protease inhibitor (PI) in combination with bosentan may increase the plasma concentration of bosentan and decrease that of the PI, unless ritonavir is included as a pharmacokinetic booster. Both PIs and bosentan are primarily metabolized by CYP450 3A4. Because PIs are also inhibitors of CYP450 3A4, they can inhibit the metabolic clearance of bosentan. Conversely, bosentan may induce the metabolism of PIs, although the effects are probably not significant in the presence of ritonavir, which is a potent CYP450 3A4 inhibitor.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster. Dose adjustment of bosentan has been recommended. In patients who are starting bosentan and have been receiving PI therapy for at least 10 days, it has been recommended to start bosentan at 62.5 mg once daily or every other day based upon individual tolerability. When starting PI therapy in patients already on bosentan, it has been recommended to discontinue bosentan for at least 36 hours prior to initiation of PI therapy. After at least 10 days following the initiation of PI therapy, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability. According to some authorities, use of the fixed combination atazanavir-cobicistat with bosentan is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14782/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[5] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[7] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Cerner Multum, Inc. "Australian Product Information." O 0[14] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[15] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767365203);
