INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14795, 'Fosamprenavir', 'Cimetidine', 'Moderate', 'Coadministration with H2-receptor antagonists or proton pump inhibitors may decrease the oral bioavailability of amprenavir from its prodrug, fosamprenavir. It is possible that fosamprenavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', NULL, 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors.', NULL, 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors. Antiretroviral response should be monitored closely during coadministration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14809/', '[1] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365203);
