INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14803, 'Fosamprenavir', 'Cobicistat', 'Moderate', 'Cobicistat may increase the plasma concentrations of antiretroviral agents. The plasma concentrations of cobicistat may also be increased or reduced in the presence of antiretroviral agents. The proposed mechanism is cobicistat inhibition of the CYP450 3A4 isoenzyme, of which antiretroviral agents may be substrates, and the inhibition or induction of CYP450 3A4 by concomitant antiretroviral medications.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor.', NULL, 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor. In addition, cobicistat should not be used concomitantly with ritonavir due to their similar effects on CYP450 3A4. According to some authorities, use of the antiretroviral combinations of atazanavir-cobicistat or darunavir-cobicistat concomitantly with the CYP450 3A4 inducers efavirenz, etravirine, or nevirapine is also not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14817/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences, Foster City, CA.[3] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Informa