INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14899, 'Fosaprepitant', 'Cobimetinib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If short-term (14 days or less) use of a moderate CYP450 3A4 inhibitor is required during treatment with cobimetinib 60 mg once daily, the manufacturer suggests reducing the cobimetinib dose to 20 mg. Following discontinuation of the moderate CYP450 3A4 inhibitor, the previous dose of 60 mg may be resumed. An alternative to the moderate CYP450 3A4 inhibitor should be used in patients who are already taking a reduced dose of cobimetinib (40 or 20 mg daily).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14913/', '[1] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14900, 'Fosaprepitant', 'Cocaine (topical)', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', NULL, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14914/', '[1] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (14901, 'Fosaprepitant', 'Colchicine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the serum concentrations of colchicine, which is primarily metabolized by the isoenzyme. Clinical toxicity including myopathy, neuropathy, multiorgan failure, and pancytopenia may occur.', NULL, 'Caution is advised if colchicine is prescribed in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if colchicine is prescribed in combination with moderate CYP450 3A4 inhibitors. In patients with normal renal and hepatic function, the dosage of colchicine should be reduced when used with moderate CYP450 3A4 inhibitors or within 14 days of using them. For the treatment of acute gout flares, the adjusted dosage recommended is 1.2 mg for one dose. Administration should not be repeated for at least three days. For the prophylaxis of gout flares, the adjusted dosage should be 0.3 mg twice a day (or 0.6 mg once a day) if the original regimen was 0.6 mg twice a day, and 0.3 mg once a day if the original regimen was 0.6 once a day. For the treatment of familial Mediterranean fever, the maximum dosage of colchicine is 1.2 mg/day (may be given as 0.6 mg twice a day) when used in the presence of moderate CYP450 3A4 inhibitors. Other significant inhibitors of CYP450 3A4 include amiodarone, dronedarone, imatinib, posaconazole, and quinupristin-dalfopristin, although the extent to which they may interact with colchicine is unknown. A similar dosage adjustment may be required. Patients should be advised to contact their physician if they experience symptoms of toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paresthesia, and numbness.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/14915/', '[1] Tateishi T, Soucek P, Caraco Y, Guengerich FP, Wood AJ "Colchicine biotransformation by human liver microsomes. Identification of CYP3A4 as the major isoform responsible for colchicine demethylation." Biochem Pharmacol 53 (1996): 111-6[2] "Severe colchicine-macrolide interactions." Prescrire Int 12 (2003): 18-9[3] Dogukan A, Oymak FS, Taskapan H, Guven M, Tokgoz B, Utas C "Acute fatal colchicine intoxication in a patient on continuous ambulatory peritoneal dialysis (CAPD). Possible role of clarithromycin administration." Clin Nephrol 55 (2001): 181-2[4] Akdag I, Ersoy A, Kahvecioglu S, Gullulu M, Dilek K "Acute colchicine intoxication during clarithromycin administration in patients with chronic renal failure." J Nephrol 19 (2006): 515-7[5] "Colchicine: serious interactions." Prescrire Int 17 (2008): 151-3[6] van der Velden W, Huussen J, Ter Laak H, de Sevaux R "Colchicine-induced neuromyopathy in a patient with chronic renal failure: the role of clarithromycin." Neth J Med 66 (2008): 204-6[7] "Product Information. Colcrys (colchicine)." AR Scientific Inc, Philadelphia, PA.[8] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[9] Cheng VC, Ho PL, Yuen KY "Two probable cases of serious drug interaction between clarithromycin and colchicine." South Med J 98 (2005): 811-3[10] Caraco Y, Putterman C, Rahamimov R, Ben-Chetrit E "Acute colchicine intoxication: possible role of erythromycin administration." J Rheumatol 19 (1992): 494-6[11] Schiff D, Drislane FW "Rapid-onset colchicine myoneuropathy." Arthritis Rheum 35 (1992): 1535-6[12] McKinnell J, Tayek JA "Short term treatment with clarithromycin resulting in colchicine-induced rhabdomyolysis." J Clin Rheumatol 15 (2009): 303-5[13] Putterman C, Ben-Chetrit E, Caraco Y, Levy M "Colchicine intox