        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332714, 'Sufentanil', 'Nifedipine', 'Moderate', 'In patients receiving chronic therapy with calcium channel blockers, the use of sufentanil during induction may lead to a higher degree of bradycardia and hypotension.', NULL, 'These patients should be carefully observed for clinically significant alterations in hemodynamics.', NULL, 'These patients should be carefully observed for clinically significant alterations in hemodynamics.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
