        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332716, 'Suvorexant', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if suvorexant is prescribed with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if suvorexant is prescribed with inhibitors of CYP450 3A4. Patients should be monitored for excessive sedation and other side effects, and the suvorexant dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332717, 'Tacrine', 'Nifedipine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332718, 'Tacrolimus', 'Nifedipine', 'Moderate', 'A retrospective study suggests that nifedipine may elevate tacrolimus whole blood trough concentrations. The mechanism is possibly due to nifedipine-induced inhibition of tacrolimus hepatic metabolism.', NULL, 'In patients receiving concomitant nifedipine and tacrolimus, renal function and tacrolimus whole blood trough concentrations should be monitored and the tacrolimus dose should be adjusted as needed.', NULL, 'In patients receiving concomitant nifedipine and tacrolimus, renal function and tacrolimus whole blood trough concentrations should be monitored and the tacrolimus dose should be adjusted as needed. If nifedipine is discontinued a tacrolimus dosage increase may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
