        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332726, 'Terazosin', 'Nifedipine', 'Moderate', 'The hypotensive effects of nifedipine and alpha-adrenergic blockers may be additive. The proposed mechanism is additive vasodilatory effects. Postural hypotension may occur.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
