        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1332730, 'Theophylline', 'Nifedipine', 'Minor', 'Limited and controversial data suggest the following: either there is no significant interaction between theophylline and dihydropyridine calcium channel blockers, or theophylline serum levels increase after the addition of dihydropyridine calcium channel blockers. Data are available for nifedipine. Theophylline dosage should be reduced if necessary, and plasma levels should be checked when clinically necessary and appropriate. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
